import React, { useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { useState } from "react";

const AdminExpenseView = () => {
    const {id} = useParams()
    const [expenses,setExpenses]=useState([])
    const navigate=useNavigate()
    const expenseView=(eid)=>{
        navigate(`/expenseview/${eid}`)
    }

    const approve=(eid)=>{
            fetch(`/approve/${eid}`,{
                method:"get",
                headers: {
                    "Content-Type": "application/json",
                },
            })
            .then((res) => res.json())
            .then((res) => {
                window.location.reload(false)
            });
    }

    const decline=(eid)=>{
        fetch(`/decline/${eid}`,{
            method:"get",
            headers: {
                "Content-Type": "application/json",
            },
        })
        .then((res) => res.json())
        .then((res) => {
            window.location.reload(false)
        });
    }
    useEffect(()=>{
        fetch(`/viewexpense/${id}`, {
            method: "get",
            headers: {
                "Content-Type": "application/json",
            },
        })
            .then((res)=>res.json())
            .then((res) => {
                setExpenses(res)
            })
    },[])

    return (
        <div style={{ width: '100%', height: '100%', display: 'flex', justifyContent: "center", flexDirection: "column", alignItems: "center", marginTop: "12%" }}>
            <div class="styled-table" style={{width}}>
                <table >
                    <thead>
                        <tr>
                            <th>Invoice Id</th>
                            <th>Date</th>
                            <th>Desc</th>
                            <th>Amount</th>
                            <th colSpan={3}>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {expenses.map(user => {
                            return (
                                <tr>
                                    <td>{expenses.invoice_id}</td>
                                    <td>{expenses.date}</td>
                                    <td>{expenses.desc}</td>
                                    <td>{expenses.anmount}</td>
                                    <td><a href="" onClick={() => expenseView(expenses.id)}>View</a></td>
                                    <td><a href=""onClick={()=>approve(expenses.id)}></a>Approve</td>A
                                    <td><a href=""onClick={()=>decline(expenses.id)}>Reject</a></td>
                                </tr>
                            )
                        })}


                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default AdminExpenseView
