import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Viewuserexpenses =()=>{
    const {id}=useParams()
    const [expenses,setExpenses]=useState([])


    useEffect(()=>{
        fetch(`/viewexpenseuser/${id}`,{
                method:"get",
                headers: {
                    "Content-Type": "application/json",
                },
        })
        .then((res)=>res.json())
        .then((res)=>{
               setExpenses(res)
        })
    },[])
        return (
            <div style={{width:'100%', height:'100%', display:'flex', justifyContent:"center" ,marginTop:"12%" }}>

              <div className="styled-table">
            <table >
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>Desc</th>
                        <th>Date</th>
                        <th>Amount</th>
                        <th>Status</th>
                    </tr>
                </thead>
                <tbody>
                    {expenses.map(e=>{
                        return(
                            <tr>
                            <td>{e.invoice_id}</td>
                            <td>{e.desc}</td>
                        <td>{e.date}</td>
                        <td>{e.amount}</td>
                        <td>{e.status}</td>
                    </tr>
                        )
                    })}


                </tbody>
            </table>
        </div>
        </div>
        )
}

export default Viewuserexpenses
