import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Comments from "./comments";
const Expenseview = () => {
    const [expence, setExpense] = useState([])
    const [comments,setComments]=useState([])
    const { id } = useParams()
    useEffect(() => {
        fetch(`/viewexpense/viewuser/${id}`, {
            method: "get",
            headers: {
                "Content-Type": "application/json",
            },

        })
            .then((res) => res.json())
            .then((res) => {
                setExpense(res)
                setComments(res.comments)
                // console.log(res)
                console.log(res.comments)
            })
    }, [])
    return (
        <div style={{ width: '100%', height: '100%', display: 'flex',flexDirection:"column", marginTop: "2%" }}>
           <label style={{display:"flex",flexDirection:"column",float:"left"}} class="exp_form_title" >Expense Details:</label>
            <div class="styled-table" style={{backgroundColor:"darkcyan"}}>
                <table>
                    <tr>

                        <th>Invoice Id:</th>
                        <th>Date:</th>
                        <th>Amount:</th>
                        <th>Description:</th>
                        <th>Status:</th>
                    </tr>
                    <tbody>

                        <tr>

                            <td>{expence.invoice_id}</td>
                            <td>{expence.date}</td>
                            <td>{expence.amount}</td>
                            <td>{expence.desc}</td>
                            <td>{expence.status}</td>




                        </tr>


                    </tbody>
                </table>
            </div>
            <div>
            <Comments commnts={comments} exp_id={id}></Comments>
            </div>
        </div>

    )
}

export default Expenseview
