import { React, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
const Admin = () => {
    const navigate = useNavigate()
    const [users, setUsers] = useState([])
    const func=()=>{
            navigate('/createuser')
    }
    const view=(uid)=>{
        navigate(`/adminexpenseview/${uid}`)
    }
    const del=(uid)=>{
        fetch(`/delete/${uid}`,{
            method:"get",
            headers: {
                "Content-Type": "application/json",
            },
        })
            .then((res) => res.json())
            .then((res) => {
                window.location.reload(false)
            });
    }

    const edit=(uid)=>{
        navigate()
    }

    useEffect(() => {
        fetch('/admin', {
            method: "get",
            headers: {
                "Content-Type": "application/json",
            },

        })
            .then((res) => res.json())
            .then((res) => {
                // console.log(res)
                setUsers(res)

            });
    }, [])
    return (
        <div style={{ width: '100%', height: '100%', display: 'flex', justifyContent: "center", flexDirection:"column",alignItems:"center", marginTop: "8%" }}>
             <label style={{display:"flex",flexDirection:"column"}} class="exp_form_title">User Details: </label>
            <div class="styled-table">
                <table >
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Department</th>
                            <th>Status</th>
                            <th colSpan={3}>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => {
                            return (
                                <tr>
                                    <td>{user.id}</td>
                                    <td>{user.name}</td>
                                    <td>{user.email}</td>
                                    <td>{user.department}</td>
                                    <td>{user.status}</td>
                                    <td><a href="" onClick={()=>view(user.id)}>View</a></td>
                                    <td><a href="" >Edit</a></td>
                                    <td><a href="" style={{color:"red"}} onClick={()=>del(user.id)}>Delete</a></td>
                                </tr>
                            )
                        })}


                    </tbody>
                </table>
            </div>
            <div style={{backgroundColor:"darkcyan",fontStyle:"italic",fontSize:"1.8rem",boxShadow:"revert",borderRadius:"15px",padding:"4px"}}>
               <a href="" onClick={func}>Add User</a> 
            </div>
        </div>
    )
}

export default Admin
